select v.dataset_id, dv.createdate, dv.modificationtime, d.identifier, dva.value, dts.alias, dts.name, dts.affiliation, f.label, f.restricted, f.version, data.contenttype, data.filesize, f.id  from datasetfield as df, datasetversion as v, dataset as d, datasetfieldtype as dt, datasetfieldvalue as dva, dvobject as dv, dataverse as dts, filemetadata as f, datafile as data
where df.datasetfieldtype_id=dt.id and  df.datasetversion_id=v.id and d.id=v.dataset_id and dt.name='title' and dva.datasetfield_id=df.id and dv.id=v.dataset_id and dv.owner_id=dts.id and f.datasetversion_id=df.datasetversion_id and f.datafile_id=data.id
